mapper.select:
select menu_id, title, name, menu_sort, component, path, type, permission, icon, cache, hidden, pid, sub_count, i_frame, create_by, update_by, create_time, update_time from system_menu
and ( title like concat('%',#{args.blurry},'%') or name like concat('%',#{args.blurry},'%') or permission like concat('%',#{args.blurry},'%') )
and pid is null
and pid = #{args.pid}
and create_time between #{args.createTime[0]} and #{args.createTime[1]}
order by menu_sort

SELECT m.* FROM system_menu m, system_roles_menus r
WHERE m.menu_id = r.menu_id AND r.role_id IN

    #{roleId}

AND type != #{type}
order by m.menu_sort

SELECT menu_id id
FROM system_menu
WHERE title = #{title}

SELECT menu_id id
FROM system_menu
WHERE name = #{name}

SELECT count(*)
FROM system_menu
WHERE pid = #{pid}

mapper.update:
update system_menu
set sub_count = #{count}
where menu_id = #{menuId}
