mapper.select:
select count(*)
from system_user u


        and u.user_id = #{criteria.id}


        and u.enabled = #{criteria.enabled}


        and u.dept_id in

            #{deptId}



        and (
        u.username like concat('%', #{criteria.blurry}, '%')
        or u.nick_name like concat('%', #{criteria.blurry}, '%')
        or u.email like concat('%', #{criteria.blurry}, '%')
        )


        and u.create_time between #{criteria.createTime[0]} and #{criteria.createTime[1]}

select u.*, j.id as job_id, j.name as job_name, r.role_id as role_id, r.name as role_name, r.level as role_level, r.data_scope as role_data_scope from ( select u.user_id as user_user_id, u.dept_id as user_dept_id, u.username as user_username, u.nick_name as user_nick_name, u.email as user_email, u.phone as user_phone, u.gender as user_gender, u.avatar_name as user_avatar_name, u.avatar_path as user_avatar_path, u.enabled as user_enabled, u.pwd_reset_time as user_pwd_reset_time, u.create_by as user_create_by, u.update_by as user_update_by, u.create_time as user_create_time, u.update_time as user_update_time, d.dept_id as dept_id, d.name as dept_name from system_user u left join system_dept d on u.dept_id = d.dept_id
and u.user_id = #{criteria.id}
and u.enabled = #{criteria.enabled}
and u.dept_id in
#{deptId}
and ( u.username like concat('%', #{criteria.blurry}, '%') or u.nick_name like concat('%', #{criteria.blurry}, '%') or u.email like concat('%', #{criteria.blurry}, '%') )
and u.create_time between #{criteria.createTime[0]} and #{criteria.createTime[1]}
order by u.user_id desc
limit #{criteria.offset}, #{criteria.size}
) u left join system_users_jobs suj on u.user_user_id = suj.user_id left join system_job j on suj.job_id = j.id left join system_users_roles sur on u.user_user_id = sur.user_id left join system_role r on sur.role_id = r.role_id order by u.user_user_id desc

select user_id as id, username
from system_user
where email = #{email}

select user_id as id, username
from system_user
where phone = #{phone}

mapper.update:
update system_user
set password       = #{password},
    pwd_reset_time = #{lastPasswordResetTime}
where username = #{username}

update system_user
set email = #{email}
where username = #{username}

mapper.select:
SELECT count(*) FROM system_user u, system_users_jobs j
WHERE u.user_id = j.user_id AND j.job_id IN

    #{jobId}

SELECT count(*) FROM system_user u
WHERE u.dept_id IN

    #{deptId}

SELECT count(*) FROM system_user u, system_users_roles r
WHERE u.user_id = r.user_id AND r.role_id in

    #{roleId}

mapper.update:
update system_user set password = #{password}
where user_id in

    #{id}
